package controller;


/**
 * This is a controller class that manage the arithmetic operations.
 * 
 * @author raszreen
 *
 */

public class NumberCalculator {
	
	
	/**
	 * A method that will get the summation from two numbers
	 * @param number1
	 * @param number2
	 * @return
	 */
	public int getSum(int number1, int number2, int number3) {
		
		return number1 + number2 + number3;
		
	}

	public int getMultply(int number1, int number2, int number3) {
		// TODO Auto-generated method stub
		return number1 * number2 * number3;
	}

}
